// Formulari de Contacte

const contactForm = {
  camps: {
    nom: {
      tipus: "text",
      placeholder: "El teu nom",
      requerit: true
    },
    email: {
      tipus: "email",
      placeholder: "el.teu@email.com",
      requerit: true
    },
    missatge: {
      tipus: "textarea",
      placeholder: "Explica'm sobre el teu projecte...",
      requerit: true
    }
  },

  validacio: {
    email: /^[^\s@]+@[^\s@]+\.[^\s@]+$/,
    nom: /^[a-zA-Z\s]{2,50}$/
  },

  enviar: async (dades) => {
    try {
      const resposta = await fetch('/api/contact', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(dades)
      });

      if (resposta.ok) {
        console.log("Missatge enviat amb èxit!");
        return { exit: true };
      } else {
        throw new Error('Error en enviar');
      }
    } catch (error) {
      console.error("Error:", error);
      return { exit: false, error };
    }
  }
};

console.log("Formulari llest per a rebre missatges");
